query DashboardSummary($organizationId: Int!) {
  organizationStats(organizationId: $organizationId) {
    totalProjects
    activeProjects
    completedProjects
    totalTasks
    completedTasks
  }

  activeProjects: projectsByOrganization(organizationId: $organizationId, status: "ACTIVE") {
    id
    name
    taskCount
    completedTasks
    completionRate
    dueDate
  }

  todoTasks: tasksByOrganization(organizationId: $organizationId, status: "TODO") {
    id
    title
    assigneeEmail
    dueDate
    project {
      name
    }
  }
}

